INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (2, 'hisenior-mega5est-7th', 'S', NULL, 'Hisenior Mega5est 7th', '-', '4.4', '', 0.79, '1 DD 2 BA 2 EST', 'You can''t go wrong with this set. a bit more sub bass than tea pro. trelble agak searah TPSE, tp highnya punya smooth dan ketajaman yg appealing. soundstage di high memang luas. tapi separasi imaging stereo TP SE lebih lebar. nuansa ruang stage bs lawan, tp stereeo imaging width TP SE msh jawara. Vocal forward and cleaner di TP SE dibanding di sini. ini Bagus di high. tp imaging masih agak ketengah dikit untuk bajetnya.', 9.472727272727273, 10, '8.2', 10, 9, 10, 'Aman', 10, 8.7, 10, 9.8, 9, 9.5, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (3, 'hisenior-mega7-very-good-cable', 'S', NULL, 'Hisenior Mega7 Very Good Cable', 7499000, '4.4 3.5', '', 0.79, '1 DD + 6BA', 'Ini searah banget sama Tea Pro SE. dan trelbnye gak se bright se lancip Astral. Pnentu utama yg bikin gw prefer  TP SE drpd ini adalah, Dynamic drivernya tea pro SE lebih bagus. rumblenya pass. gebukannya tactile mantul enak. dengan mids yang menurut gw juga porsinya ok. Hihgnya kurang lebih masih searah. Penentu utama buat lu pilih ini. aksesoris kabelnya ganteng. rasanya kaya megang levelan kabel Eletech or Effect. cakep. kabelnya kaya Electec Socrates bahannya. ', 9.418181818181818, 9.2, '8.5', 10, 9, 10, 'Aman', 9.8, 8.8, 10, 9.8, 9, 9.5, '0.95', 'Sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (4, 'mangird-tea-pro-holographic-imaging-stage-iga-massardi-gerald-barasuara', 'SS', NULL, 'Mangird Tea Pro Holographic imaging & Stage - Iga Massardi - Gerald Barasuara', 5800000, '4.4 3.5', '', 0.95, '2 Dynamic 6 Knowles BA', 'ENDGAME UNDER 10Jt for me subjectively. coherency better than Hype 4, davinci, oracle 3, EJ07m. Bass yg proper gak lebay tapi ttp fun dibanding pilgrim silver. vokal nya terpisah dari bass walaupun kuantitas bassnya bener2 dispersi sebarannya lebar dalem punchy. gak bleed ke vocal. kaya spiker panggung. bass di send aux ke sub. vokal tetep di PA. separasi soundstage. better than Tea 2. clarity naik jg. volume linerarity kenceng pun masih aman khas Mangird. luas enak. gausah ganti kabel. kabel detach. definitely Value di atas harganya. REVIEW YOUTUBE', 9.172727272727272, 9.5, '8', 10, 9.2, 9, 'Aman', 9.7, 9, 10, 8, 9, 9.5, '0.9', 'Sedang ke Kencang', 'Pentaconn Coreir full grippy fit. less sub. more mid', 'Eletech Socrates (spacious soundstage)', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (5, 'xenns-top-pro-asteriska-tj-barasuara', 'S', NULL, 'Xenns Top Pro Asteriska & TJ, BARASUARA', 8190000, '4.4 3.5', '', 0.75, '2 DD 8 BA', 'Timbre bagus, koherensi bagus. problemnya menurut gw cuma di treble aja kok. ini ujungnya agak lebih lancip. kalo lu dengerin di high volume dan sensitif di 10k ke ats ya mungkin terlalu airy. tapi ada ternyata yg demen kaya gini. gw sendiri ga terlalu. pengen upper mids to treblenya kaya JM1 or Meta tuning yang mana gradually turun gitu gak nanjak2 tinggi lagi. ke https://squig.link/lab/fernandagunsan/ aja klik yang JM1 nya. overall timbre lebih bener ga plasticky deibanding Tea Pro tp improvement imaging dan soundstage nya menurut gw ga ada. dan cleaner bass mid bass. kalo lu pgn yang feelingnya clean gak se bass tea pro ya ini bisa jadi solusi. tp ingat treblenya di ujung bgt naek. gak di area shouty/sibilance seperti 4-8k. tapi di 10k n above', 9.4, 10, '8.5', 10, 8, 10, 'Aman', 10, 9, 10, 9.4, 9, 9.5, '0.95', 'Sedang ke pelan', NULL, NULL, NULL);
