INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (12, 'ziigaat-odyssey-2-new-recommended', 'A++', NULL, 'Ziigaat Odyssey 2 NEW RECOMMENDED', 4100000, '4.4 3.5', '', 0.95, '1 DD + 3 BA', 'Bagus. JM1 Target. Better timbre and fit than Daybreak. Technicality slightly better than Daybreak, but the price is waaayy higher bruv. Full review di sini', 8.954545454545455, 9, '8.6', 9.5, 9, 9, 'Aman', 9, 9, 9, 8.5, 9.5, 9, '0.8', 'Kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (13, 'epz-p50', 'S-', NULL, 'EPZ P50', 3200000, '4.4 3.5', '', 0.8, '1 DD + 2BA + 2Planar', 'Rich highs, great clarity n resolution. wider, and more spacious than 61t. impact bass tidak hilang, well extended tp gk jadi highlight utamanya. transient bass cepet, detail. open sounding jernih. dengerin rock crunchy. kek nyalain overdrive tipis buat bikin garang. or kaya kalo mastering dikasih tape saturation. highnya bagus gk kaya Supermix 4 pokonya. dan gak kerasa thin juga. emphasize utamanya di mid to high bagus. mid bas to sub gak jadi pemeran utama tp gak jelek jg cukup aja perannya', 8.700000000000001, 9, '8.6', 9.5, 9, 9, 'Aman', 8.5, 8, 8.7, 7.9, 8.8, 8.7, '0.9', 'Sedang ke Kencang', 'Spinfit Neo', 'Verus Magni C4 Rhodium', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (14, 'xenns-mangird-tea-2', 'A-', 'S', 'Xenns Mangird Tea 2 ', 4700000, '4.4 3.5', '', 0.75, '1dd + 2 Sonion BA + 4 Knowless BA', 'One of highly regarded underrated IEM in the audiophile world. lebar megah jauh. separasi instrumen, timbre, detail treble yang lebih oke.deep sub bass. good punch. teknikalitas luar biasa holographic. semua tone, notes guitar dan keyborad ketika main bareng. bakal terdengar jelas mana yang gitar mana yang keyboard. voc cowo jadi lebih jernih. better lower mid. tebal. better bass dan soundstage juga dibanding blessing 3 (Preferensi pribadi)', 8.981818181818182, 8, '10', 9.5, 8.5, 9, 'Aman', 9, 9, 9.5, 7.8, 9.5, 9, '0.9', 'Kencang', 'Acoustune AET08 (less treble add soundstage) Acoustune AEX07 (neutral bass, mid centric) JVC Spiral Dot ++ (jernih, less soundstage) Softears UC (jernih, midrange centric, less bass) Dunu S&S (+ Upper mids + treble, Comfort) ', 'Sena Rinjani (tight energetic clean) Sena Toba (Relaxed, Clean)', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (15, 'dunu-davinci', 'A', NULL, 'Dunu Davinci', 4999000, 'Modular 3.5 & 4.4', NULL, NULL, '2 DD + BA', 'Basically a more bassy Tea 2. ', 9.136363636363635, 9.5, '10', 9, 8.5, 8.5, 'Aman', 9.5, 9.5, 9.5, 8, 9.5, 9, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (16, 'epz-530', 'S', 'S', 'EPZ 530', 6900000, NULL, NULL, NULL, '5 Balanced Armatures', 'SUPERB IEM FITTING and versatility with its cable. bisa ganti plug 3.5,2.5 dan 4.4 praktis. Suara smooth. di upper mids , treblenya pun sparkly tapi smooth gak nyerang. ga cring nusuk. bagus pula timbre nya untuk ukuran full 5BA. punch nya bagus. sub bass cepet. sayang kurang dalem aja dikit karena gak ada DD sih. tapi fittingnya terlalu juara untuk diskip dan suaranya bersahabat banget buat monitoring!', 8.954545454545455, 8, '9', 9, 9.5, 9, 'Aman', 9, 9.5, 9.5, 9, 8, 9, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (17, '64-audio-u12t', 'A', 'S', '64 Audio u12t', 30000000, NULL, NULL, NULL, '4 Low Knowles BA 6 Mid Knowles BA 1 High Knowles BA 1 Tia Driver (BA)', 'I''m gona say that this is the best traditional IEM ever existed. (or I have ever tried). karakter lega lepas dan OPEN banget di high itu gak ada yang bisa bikin lagi kecuali si 64audio ini. khas banget. all Balance armature driver tapi bassnya cukup banget. dan udah oke banget. balance. cepet. jernih. gak sakit. fittingnya juga gak sebesar u18s or u18t that''s why gw taro ini di sini. ada teknologi LID buat didengerin di source apapun suaranya tetep best quality. ada teknologi APEX, buat gonta ganti modul kekedapan suara lingkungan. yang bisa naekin or nurunin bass juga.', 9.18181818181818, 8, '8', 9.5, 10, 9, 'Aman', 9.5, 9.5, 9.5, 9, 10, 9, NULL, NULL, 'Bawaan aja. soalnya dari 64audio nozzlenya agak beda jadi agak sulit kalo mau diganti merk lain', 'Bawaan', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (18, 'sennheiser-ie600', 'A', 'S', 'Sennheiser IE600', 12900000, '3.5', NULL, NULL, '1 Sennheiser In-house Dynamic Driver', 'The Best Single DD I have ever tried. vokalnya gak kepisah maju kaya merk merk china pada umumnya.. teknikalitas juara. bassnya cepet.. very well balanced. sudah pasti koheren karena gak perlu dicrossover driver lain. resolusi driver. teknologi sennheiser transducer back volumenya researchnya. QC nya top German quality (confirm udah ngobrol sama petinggi2 sennheiser wakil southeast asia) waktu gw datang ke acara launching sennheiser MTW3 di agustus 2022', 8.363636363636363, 8.5, '8.5', 9, 8, 8.5, 'Sedikit', 8, 8, 8, 8, 9, 8.5, NULL, NULL, 'Bawaan aja. dia eartipsnya uniq. Bisa meleyot dipendekiin bisa di extend manjang sendiri. ketika mode pendek bikin lebih berbody texture suaranya lebih tebel tapi jadi berkurang soundstagenya. ketika mode extend, nambahin soundstage', 'Bawaan', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (19, 'moondrop-blessing-3-tenz-valorant-gaming', 'A', 'S', 'Moondrop Blessing 3  Tenz Valorant Gaming', 4769000, '3.5', '', 0.65, '2 Horizotanly opposed dual DD 4 BA', 'asih agak dekat tuningan dengan blessing 2. yang cenderung neutral with bass boost. click sini tapi bassnya DEEP. cepat. TERASA walau ya ga banyak. clean upper mids. better than Tea 2. Clarity okay. kekurangannnya bagi gw. lower mid yang kurang tebal belum kaya Tea 2 (preferensi pribadi), sbagian orang mungkin bassnya tetep kurang banyak. but this is highly coherent. crossover bagus. ga ada rasa2 frequency hilang. soundstage cakep. walau better tea 2 sedikit tetep. buat yg masih suka ada bassnya ambil tea 2. buat yang pengen upper midsnya terang clean dan cenderung neutral suka vokal only, ambil ini. untuk kelasnya sama. tinggal preferensi aj', 8.409090909090908, 8, '8.5', 10, 9, 9.5, 'Aman', 8.5, 8, 8, 7.5, 7.5, 8, '0.85', 'Sedang', 'Kekurangannnya. ujung nozlenya loose. gak ada sangkutannnya kaya Tea 2. jadi mesti ati2. cari eartips yang GRIP nya greget. gw pakein AET08 soalnya gak bisa dia. gampang loose', 'Senacoustic Copper Verus Magni Eletech Fortitude Copper XINHS Single Crystal Copper', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (20, 'simgot-supermix-5-new', 'A-', NULL, 'Simgot Supermix 5 NEW', 3560000, 'Modular 3.5, 4.4', '', 0.75, '1 BC + 1DD + 2BA + 1 PL', 'lower treble ke upper trelblenya rolloff bagus. midbass to sub bass nya udah clean ud searah sama Tea Pro SE, Mega7, dll lah. tp Uppermdsnya terlalu menggunung tinggi. cek aja di Squiglink gw dah. dan air treble menurut gw masih bisa di boost lagi. Kekurangan utama di uppermidsnya yg masi tinggi 3k-4k. fit agak gede. dengan bone conduction yang masi suka agak dengung2 kalo disentuh', 8.10909090909091, 7.8, '8', 7.5, 8, 9, 'Aman', 8, 8, 8, 7.9, 8.5, 8.5, '0.8', 'Sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (21, 'ziigaat-odyssey', 'A-', NULL, 'Ziigaat Odyssey ', 3700000, '3.5', '', 0.65, '3BA + 1DD', 'Koherensi 80% aman. Bass gede gemuk. menurutku masih lebih rapih transisi crossover BA ke DD nya di 61t, lebih buanyak highs dr 61t. Bagus. bisa buat monitoring fitting better dr 61t. Timbre lebih natural 61t, u can hear the mid bass and mids more di 61t. ini lebih buanyak dan gemuk bass, dan lebih ngecring di atas. Fit better dr 61t. Teknikal better ini. di atas ini EPZ P50 luas puas. REVIEW YOUTUBE DI SINI', 8.427272727272726, 7.8, '8', 9.5, 8.5, 9, 'Aman', 8, 8, 8, 7.9, 9.5, 8.5, '0.8', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (22, 'kiwi-ears-quintet', 'A-', 'A', 'Kiwi Ears Quintet', 3290000, '3.5', '', 0.55, '1DD, 2 BA, 1 PL, 1 Piezoelectric', ' cukup koheren gak belang2 ada rasa2 hilang. sub tebel dalem tapi bukan basshead. treble nya jelas. greng bagus. yg pengen ujung sub kece dengan treble yang jelas banget sikat ini oke PZT dan Planarnya di treble bagus bgt. jernih but careful kalo suka volume kenceng bisa fatiguing. kekuranganya bagi gw Mid bassnya aja kurang punch sama low mid nya gak se crunchy dan tebel kaya tea 2. energy nya hanya di sub bass, dan di treble. mid nya kurang berdaging. Timbre buagus', 8.318181818181818, 8.5, '8', 8.5, 9, 8.5, 'Aman', 8.5, 8, 8, 8.5, 7.5, 8.5, '0.8', 'pelan', 'Acoustune AET08 (M)', 'Senacoustic Copper', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (23, 'juzear-41t', 'A+', 'A+', 'Juzear 41t', 2399000, '3.5', '', 0.75, '1 DD + 2 Juzear BA + 2 Sonion BA', 'FINALLY MINI TEA 2!. Dia Warm balance. tuningan searah banget sama tea 2. kalo SR5 ada tajem2 nya di 8khz dan kekurangan lo mid. ini gak sama sekali. tuningan 4BA 1DD paling  sesuai sama gw dan untuk monitoring pun juga enak banget. akurat. imaging soundstage bagus. tapi belum segarang, secrunchy se lebar luas Tea 2. Is it better than SR5? For me yes. tapi for some mungkin kekurangan brightness di treble 8k. Fitting EDAN buat monitoring. gausa gonta ganti kabel/tips udah enak Review Youtube', 8.018181818181818, 8, '8.5', 8, 7.7, 8, 'Aman', 8.5, 8.5, 8, 8, 7.5, 7.5, '0.75', 'kencang', 'Acoustune AET08 (M)', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (24, 'pula-pa02', 'A+', NULL, 'Pula Pa02 ', 2349000, '3.5', '', 0.65, '1DD + 4 BA', 'Bagus. midrange neutral. natural. ada emphasize vokal more. dan sizzle treble yang lumayan boosted. tapi gak seboosted Supermix4. transisi dari uppper mids/treble ke low agak ada kurang dikit. kurang fuller sounding. koherensi sebenernya udah oke. but could be better. buat yg ga terlalu bisa dengerin koherensi mgkin jg ga ketara banget. bagus kok. natural dan cakep faceplate juga fitting buat manggung enak', 7.963636363636363, 8, '8.5', 8, 8, 8, 'Sedikit', 7.8, 7.8, 8, 8, 7.5, 8, '0.7', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (25, 'sound-rhyme-sr5', 'A+', 'A+', 'Sound Rhyme SR5  ', 2349000, '3.5', '', 0.7, '4 Knowles BA 1 DD', '4-way Crossver HIGHLY COHERENT. gak berantakan gak ada rasa-rasa belang2 frekuensi hilang buat 2 jutaan 5 driver ini highly coherent. bright at 8k. ada a tad bit boost di 8k but not piercing but dominan. smooth top end treble di simbal. jernih gak nyerang. midrange bagus. denger gitar garang BA KNOWLES BABY! sub bass boost ga banyak tapi DD qualitynya superb. dalem tebel. sub bass centric (under 80hz) mid bass ga banyak. di 100-150hz nya. vokal cakep bass dapet.  Review Youtube', 7.981818181818181, 8, '8.5', 8, 7.5, 8, 'Sedikit', 8.5, 8, 8, 8, 7.5, 7.8, '0.75', 'sedang ke pelan', 'JVC Spiral Dot ++ (less bass, more mid bass. Good fit, deketin telinga) Tangzu Sancai Wide (kurg lebi kaya JVC Spiral) Softeats UC (less treble, good fit - ngejauhin telinga)', 'Senacoustic Rinjani', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (26, 'gizaudio-chopin', 'A', 'A', 'Gizaudio Chopin ', 3100000, '3.5 4.4', '', 0.75, '3 BA + 1 DD', 'Pecinta vokal yg sangat2 maju + Sub bass banyak tapi tetep balance bukan bass head bisa ambil ini. tuningannnya gizaudio banget sub bassnya dalem. banyak. Tapi blm bisa dikategorikan basshead. hal tergaenaknya adalah fitting. bentuknya kae gantung di telinga gak meluk nempel kaya SR5', 7.909090909090909, 8, '8', 8.5, 7, 7.5, 'Aman', 8, 8, 8, 8, 8, 8, '0.8', 'sedang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (27, 'tangzu-heyday', 'A', 'A', 'Tangzu Heyday', 3000000, 'Modular 3.5, 2.5, 4.4', '', 0.8, '1 Planar', 'Now this is the best planar saat ini yang pernah gw coba. gausah ke timeless lagi. or raptgo or TRN Kirin, or Tin p1. sudah ini saja. enak. terang jelas, cakep. dan teknikalitas soundstagenya luar biasa bagus. holographic fast. crunchy. enak banget buat vokal. all genre. bassnya cepet. buat rock distorsi, fuzz, overdrive cakep gilak. Ruangnya sih. reverb dan soundstagenya itu megah banget2. Pakai AET08 kurangi treblenya jadi pas banget. gak  sakit. tapi jadi gak kurang juga.', 8, 7.5, '8', 8, 8, 8, 'Sangat Sedikit', 8.5, 8.5, 8.5, 7, 7.5, 8.5, 'Single Driver Tidak Perlu Bahas koherensi 100% tidak perlu dikoherensikan dengan driver lain', 'sedang ke kencang', 'Acoustune AET08 (M) Softears UC', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (28, 'truthear-nova-dipakai-barasuara-vega-antares-dewa', 'A', 'A', 'Truthear Nova  Dipakai: - Barasuara - Vega Antares (Dewa)', 2376500, '3.5', '', 0.45, '4BA + 1DD', 'vs SR5 ni lebih open atasnya. staging upper mids nya. vokalnya. dan lebih dalem banget sub bassnya. vokalnya lebih mundur dibanding SR5 yg lbh maju. yang ngerasa SR5 bassnya kurang punch nya bisa ambil ini. cukup fun sounding. buat yang suka volume santai enak bgt ini. Kalo demen kenceng bgt volume bisa akan agak shouty/sibilans d upper mids. Thicc buat dengerin gitar juga. dipakai Iga Massardi, Gerald Situmorang, Vega Antares, TJ Kusuma, Ichyl Asteriska Review Youtube', 7.990909090909091, 8.5, '8', 8, 7.9, 8, 'Sedikit Sekali', 8.5, 8, 8, 7, 8, 8, '0.75', 'sedang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (29, 'tangzu-xuan-nv', 'A++', NULL, 'Tangzu Xuan NV ', 1320000, '3.5', '', 0.8, '2 DD', 'NATURAL TIMBRE DYNAMICS! Separasi jelas. timbre natural. megah mewah. agak berat didrive. better than DZ4, C3, dan timbre natural nya di bagian high lebih asyik mnurut gw dibanding sonus. Sonus sekelas dengan ini tapi representasi imaging gw prefer ini. treble BA vs Treble DD nya itu selera masing2. tp agk less mid bass di sonus. ini lebih thick tp gak muddy. dan lebih aman dari shouty di sonus. itupun sonus mesti lu gedein kenceng banget si baru berasa shouty. kekurangannya ini agak berat di drive. Review Youtube', 7.845454545454544, 7.9, '8', 8, 7.5, 7.5, 'Aman', 7.3, 8, 7.8, 8.5, 7.8, 8, '0.9', 'sedang ke kencang', 'Bawaannya Dapat tangzu sancai. Udah oke', ' VJC Prabu Rhodium Senacoustic Toba VJC BPJS Merah 4.4', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (30, 'soratune-yi01', 'A++', NULL, 'Soratune Yi01 ', 1139000, '3.5', '', 0.75, '2 DD', 'ini, ew300, dan xuan. Sekelas. bass paling bagus kalo cari fun. rumbling, timbre bass. teknis bass bagus. dengan teknikalitas keluasan kelegaan treble yang juga cakep banget. Volume linearity gak perlu sekenceng Xuan nv. kalo suka volume kenceng, treble lebi aman xuan. Fitting lebih ok jauh Xuan dan EW300. Gw nemu 2 DM Followers bilang mudah "imbalance" (you''ve been warned) gw baru coba ditoko. Fun dan teknikal bagus parah. timbre dd yg slightly gak seflat xuan. lebih ada ngecringnya dr xuan, lebih ada bottom nya dr xuan. tp sizzle trelble timbrenya gak semetalik ew300. BL02 nya skip aja, too vshape ish with metalik timbre', 7.818181818181817, 8.2, '7.5', 8, 7, 7.5, 'Sedikit Sekali', 8, 8, 7.8, 8, 8, 8, '0.9', 'sedang ke kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (31, 'simgot-ew300-dipakai-reality-club', 'A++', NULL, 'Simgot EW300 Dipakai Reality Club ', 1119000, '3.5 Type C', '', 0.75, '1 DD + 1 Planar + 1 Piezoelectric', 'Searah xuannv, lebih airy treblenya lebih less natural timbre treblenya tapi lebih open sounding soundstagenya. Cuma midbassnya jadi agak crumpled muffled banget sih enggak. cuma lebih banyak dr xuan nv. Xuan NV d mid bass lebih cleaner. Imaging about the same. easier to driver jg dr Xuan NV. Bass mid bass prefer xuan NV. upper mids to treble technicality soundstage better ini. Naturalness timbre di treble better Xuan NV. Choose your poison. Review Youtube warna hitam HBB sama ga beda suara', 7.818181818181817, 8, '7.5', 8, 7.5, 7.5, 'Aman', 7.7, 8, 7.8, 8, 8, 8, '0.9', 'sedang ke pelan', 'JVC Spiral Dot ++ (less bass, great fit, more neutral)', 'VJC Prabu Rhodium Senacoustic Toba', 'EQ EW300');
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (32, 'simgot-eg280-include-dsp-eq-dongle-dac', 'A', NULL, 'Simgot EG280 Include DSP EQ Dongle DAC', 1200000, '3.5 Type C', '', 0.7, '1DD + 1 Planar', 'Ok juga loh ini, tuning agak searah EW300. agak banyak ujung teblenye bisa bahaya. Tapi dia dapet dongle DAC yang bisa parametric EQ 8 band. dan ada mic nya. micnya bisa diatur gainnya lewat appnya. keren Simgot gw pikir appnya gabisa saingin Fiio/moondrop tp bagus bgt appnya. sayang DAC ampnya kecil kaya Moondrop Echo. mgkin kalo se akliam pd6 bisa lebih luas lagi tp ya harganya takut kurang ok. mid bass to sub bass masih searah ew300 or LM500 jg. mid ke cut di tengah skitar 800. dan naek gradually dari  lowmid ke sub. ', 7.745454545454546, 8, '7.5', 8, 7, 7.5, 'Aman', 7.6, 8, 7.8, 7.8, 8, 8, '0.85', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (33, 'agasound-sublimation-new', 'A', NULL, 'Agasound Sublimation NEW', 1425000, '3.5', '', 0.4, '1 DD', 'Ini tipe yang sangat uppermids to low treble centric. jadi jernih dan maju di vocal. kalo lu pencari timbre yg natural bgt. menurut gw ini masih ada a hint of "metalic" Shrill di sekitar 4-6k nya. dia agak counternya ew300, yang banyak mujung sub dan ujung treble tp mids/upper mids nya gak terlalu "ngisi" nah ini si sublimation, gigit mentereng bgt uppermids to low treblenya.', 7.772727272727271, 7.8, '7.9', 7.9, 7.2, 7.5, 'Aman', 7.8, 8, 7.8, 7.6, 8, 8, 'Single Driver', 'pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (34, 'etymotic-er4xr', 'A+', 'A', 'Etymotic ER4XR', 4000000, '3.5', '', NULL, '1 BA', 'THE ONLY REAL NEUTRAL IEM. cuma ini saja. bukan Dunu titan s, bukan hz sound mirror. bukan. hanya ini. kekurangan deep insertion aja kalo belom biasa ya ga nyaman. tapi itu teknologi yang bikin treble roll offnya jadi pas dan netral', 7.590909090909091, 7.5, '7.5', 8, 8, 8, 'Aman', 7.5, 7, 7, 9, 6.5, 7.5, NULL, NULL, 'Triple Flange Etymotic', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (35, 'raptgo-hook-x', 'B-', 'B', 'Raptgo Hook X', 3000000, '3.5', NULL, NULL, '1 Planar Piezo Electric', 'Ini juga bagus sih compared to Heyday gw prefere heyday sedikit. ada yang ngebikin upper midrangenya jadi agak muffled/warm sedikit.. but If you can;t stand Heyday, maybe this one is for you', 7.727272727272727, 7, '7.5', 8, 8, 7, 'Aman', 8.5, 8.5, 8, 7, 7, 8.5, NULL, NULL, 'Acoustune AET08', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (36, 'simgot-supermix-4', 'B-', NULL, 'Simgot Supermix 4', 2450000, '3.5', '', 0.3, '1 DD, 1BA, 1 Planar, 1 Piezo', 'too bright. maybe gak too bright sih lebih banyak emphasize more aja di treble ujungnya. dan kurang natural gimana gitu treblenya.t api tekinkalnya sebenernya oke. cuma midbassnya turun lemes. dikencengin volumenya ya kena bright dluan. tekins treble nya emang luas open lebar, clarity resolusi mantep, tp tuning bright ya bright. ntah tuning, ntah driver, ntah crossover bikin a bit bright. Cek d toped ud bnyk yg nyesel blind buy dan ada sekendnya. Better Xuan NV, Nova, Chopin, SR5, all the way kalo gw. subjektip Review Youtube', 7.409090909090909, 7, '7', 7, 6, 8, 'Lumayan Ada', 8.5, 8.5, 8, 7, 6, 8.5, '0.8', 'pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (37, 'juzear-defiant', 'B+', NULL, 'Juzear Defiant', 1639000, '3.5', '', 0.75, '1DD + 3 BA', 'Tuning no problem. masih se arah2 dengan 61t dikit. tapi koherensi agak kurang. low mid agak ilang. banyak emphasize di vokal. tapi timbre nya lumayan buruk. sangat berasa plasticknya. dan agak kehilangan definisi dari biasanya di iem multi BA pada bajet 3 juta ke atas. mid nya kurang rapet. kurang well defined. kurang nyatu. tuning ok. kalo butuh looksnya aja, dan tuning aja silakan. my choice ttp trio xuan, ew300, soratune', 7.354545454545454, 8, '7', 7, 7, 7, 'Aman', 7.5, 8, 7.9, 6.5, 8, 7, '0.4', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (38, 'pula-unicrom', 'A-', NULL, 'Pula Unicrom', 1281000, '3.5', '', 0.75, '1 DD', 'Good driver. tuning searah Inawaken Dawn ms, tipe2 yang midbass naek early, tapi bassnya agak lambat aja recovery impactnya. jadi kesannya kaya banyak midbass to sub nya. kalo lu tipe yg pengen midbassnya clean. ini kurang cocok. bisa ke xuan nv/soratune. tidak se airy ew300 jg. average soundstage dan imaging. tp decent aja sih buat harganya. ', 7.490909090909091, 8, '7.5', 7, 7.4, 7, 'Aman', 7.5, 8, 8, 7, 8, 7, 'Single Driver', 'sedang ke kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (39, 'simgot-em6l', 'A-', 'A', 'Simgot EM6L ', 1600000, '3.5', '', 0.75, '1DD + 4BA', 'Ini baru best sejutaan hybrid BA + DD.. gak bright amat kaya EA500. Lebar. Warm + Balance. itu sudah pasti kesukaanque. hajar volume kenceng oke. 3d Width and Depth nya juga bagus nih positioningnya. Koherensi asyik. Gausah beli Tanchjim Kara, Kailua. udah ini aja. ganteng asli. Kekurangan?Buat pecinta neutral bright2 gitu mgkin bisa jadi terlalu warm.', 7.454545454545454, 8, '7.5', 7, 7, 7, 'Aman', 7.5, 8, 8, 7, 8, 7, '0.65', 'sedang ke kencang', 'JVC Spiral Dot ++', 'Senacoustic SPC + Rhodium Xinhs Single Crystal Copper', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (40, 'moondrop-kadenz-new', 'B-', NULL, 'Moondrop Kadenz NEW', 2900000, '3.5 Type C', '', 0.8, '1 DD', 'Moondrop VDSF. seperti biasa masih gak suka. masih terlalu midrangeyyy. sub gak banyak. sangat amat moondrop lah pokoknya. untuk 1 dd di bajet 2 juta gw gak sarankan. valuenya agak kemahalan. fitting kurang enak. kabelnnya juga kaku kaku berat gitu. dongle DSP nya oke. tapi patut diakui soundstage. imaging lega. mid nya terang jelas jernih walau bassnya gw rasa kurang punch di mid bass to sub bassnya masih perlu ditingkatin lagi buat selera gw pribadi. ', 7.590909090909091, 7, '7', 7.5, 7, 7, 'Aman', 8, 8, 8.5, 7, 8, 8.5, 'Single Driver', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (41, 'tangzu-zetian-wu', 'A', 'A', 'Tangzu Zetian Wu', 1955000, '3.5', '', 0.65, '1 Planar Driver', 'Single Planar pertama yg gw bisa denger pas pertama coba tanpa ada basa basi banyak kekurangan. sub bass oke agak banyak tp karena planar jadinya cepet. asyk banget. gitar elektrik crunchy, stage lebah megar. butuh power agak lebih menurut gw biar jadi makin enak. SUPERB CLARITY. jernih banget walau kena rekaman yang butek/warm. sub bass lebih banyak dibanding yang versi heyday nya.', 7.636363636363635, 7, '7', 7, 7, 7, 'Aman', 8.5, 8.5, 8.5, 7, 8, 8.5, 'Single Driver', 'sedang ke pelan', 'Acoustune AET08 (M)', 'Senacoustic SPC + Rhodium', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (42, 'shuoer-s12', 'A', 'A', 'Shuoer s12', 1785000, '3.5', '', 0.65, '14.3mm Planar Magnetic', 'denger rock di sini asyik. timbre planar emang beda. Lo harus coba planar at least satu. Planar Tuh beda. cepet dia transientnya.. gak distorted. tapi jernih dan twangy gitar mantep di sini. cuma di s12 agak tinggisedikit aja treblenya yaahh.. yang s12 pro gw gasuka. lebih tajemlagi treblenya dari ini. paling bagus ambil yang pink kalo bisa', 7.409090909090909, 7, '7.5', 7.5, 6, 6.5, 'Sedikit', 8, 8, 8, 7, 8, 8, 'Single Driver', 'sedang ke pelan', ' Dekoni Mercury Foam (M)', 'Kabel bawaann aja udah enak', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (43, 'moondrop-aria-2', 'B-', 'A', 'Moondrop Aria 2', 1450000, '3.5', '', 0.65, '1 DD', 'Ini Single DD yang teknikalitasnya bagus. midsnya bagus. compare to aria 1 gw lebih suka kuantitas bass aria 1 dan aria 1 lebih ngecring ujung treblenya. bisa agak perih di gua. di aria 2 teratasi dengan baik upper mids to treblnya. tapi sayang sub bassnya dikurangi juga. padahal di aria 1 menurut gw pas dan jadi tetep fun. buat yg ga suka sub bass banyak dan fokus vokal tebel. aria 2 ini oke kok', 7.363636363636363, 7, '7.5', 8, 7, 7, 'Aman', 7.5, 7, 8, 8, 7, 7, 'Single Driver', 'sedang ke kencang', NULL, NULL, NULL);
