INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (64, 'epz-q5-pro', 'B', 'B', 'EPZ Q5 Pro ', 699000, '3.5', '', 0.15, '1 DD', 'Lebih bright dari C3. cocok d volume rendah/moderate. kalo kenceng bgt bisa perih. cakep. lega. salah satu iem yang megah open sekali di upper midsnya. keluhan gw cuma satu yaitu konektornya MMCX, gak buruk sih cuma jarang aja opsinya. fitting oke. tapi lebih kedap dan snuggle pakai C3 kalo soal fitting. compare to EW 200. timbrenya jadi metalik. sub bass ew200 kurang extend gak sedalem ini.', 7.163636363636363, 7, '8', 7.8, 7, 7, 'Aman', 7, 7, 7, 7, 7, 7, 'Single Driver', 'pelan banget', NULL, 'Senacoustic SPC + Rhodium ', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (65, 'letshuoer-dz4', 'B', 'B', 'Letshuoer DZ4', 1279000, '3.5', '', 0.7, '3 DD + 1 Passive Radiator', 'Ini iem bagus sebenernya. lebih ke "unik". dia warm tapi teknikalitas baassnya menurut gw dalem ganteng. getaran oke. timbre gak metalik juga. oke banget lah. desain juga cakep. ini kalo lu mau cobain iem beda gitu 3 dd + 1 passive radiator as sidegrade as koleksi. cobain deh. cakep menurut gw walau warm banget. bassnya rapih rumblenya asyik.', 7.245454545454544, 8, '7.5', 7, 6.8, 7, 'Aman', 7.4, 7, 7, 7, 8, 7, '0.85', 'sedang ke kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (66, 'simgot-ea500lm', 'B-', 'B-', 'Simgot EA500LM', 1150000, '3.5', '', 0.7, '1 Dual Magnetic Circuit DLC Dynamic', 'Clean and warmer dari EA500 biasa. jadi yg lama gw ilangin ya soundstage cakep. dimensi ruangnya lega. enak. jernih. ini kaya kalo lu mau upgrade yg fokus tonal upper midrange vokal sentrik, kaya C2 or Moondrop Lan. nah upgrade ke sini udah. bersih banget. tapi megah. gak grainy. sub bass gak banyak. tapi mid bass atau low midnya tetep punchy. agak bright. saran ambil. EPZ Q5 aja. menurut gw lebih better valued.', 7.327272727272726, 7, '8', 8, 7, 7, 'Aman', 7.8, 7.8, 7, 7, 7, 7, 'Single Driver', 'sedang ke pelan', 'Eartips bawaan is okay. ', NULL, NULL);
