INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (68, 'tanchjim-oxygen', 'C-', 'B', 'Tanchjim Oxygen', 3060000, '3.5', '', 0.7, '1 Carbon nanotube dual-chamber dual brake dynamic', 'clean DD. sayang sih tapi DD harga segini sekarang lu bisa dapat multi BA + DD nabung dikit lagi ke 4 jutaan sekalian. kalo lu brangkat dari C3 or Simgot EA500  ke sini ga upgrade banyak. bass could be too thin for some people', 7.209090909090909, 7, '7', 7.5, 7, 7, 'Aman', 7.5, 7.5, 7, 7.8, 7, 7, 'Single Driver', 'sedang', 'Acoustune AET08 - Mid BASS + Soundstage Final Audio Type E - Warm + Sub bass ext', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (69, 'truthear-zero-red', 'B', 'B+', 'Truthear Zero Red', 880000, '3.5', '', 0.7, '2DD', 'Jauh lebih bagus dari yang biru. compare to EW200 sub bass lebih berasa extend lebh dalem depth nya. dan separasi juga. terhitung better balancenya dari yang ori. vokalnya lebih cakep. midrangenya lebih kedengeran. separasii lebih kepisah2 gak glued together kaya EW200. soundstagenya juga cakep banget. kalo masih suka tuningan balance tapi pengen cenderung fokus sub bass 100hz ke 60hz ambil ini. kalo fkusnya mid bass  kaya 100,125,250an dan less sub bass ambilnya ew200. upper midrange timbrenya prefer C3. Review Youtube', 7.345454545454544, 7.5, '7.5', 7.8, 7, 7, 'Aman', 7.5, 7.2, 7.8, 7, 7.5, 7, 'Single Driver', 'sedang ke kencang', 'Eartips bawaan is okay. ', 'VJC. BPJS Merah/hijau', NULL);
