INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (71, 'truthear-hexa', 'B-', 'B', 'Truthear Hexa', 1280000, '3.5', '', 0.5, '1DD + 3 BA', 'Buat yang mau upgrade ke IEM 1 jutaan dan pengen cobain rasain Balance Armature kae gimana. ya bisa coba ini. balance. bagus. BA nya garang! buat lagu apapun vokal enak. gitar2 crunch and distortion enak. soundstage average gak lebar amat kabel bawaan kurang ok. soundstage better di Simgot EM6L', 7.19090909090909, 7, '7.5', 7.6, 7, 7.5, 'Aman', 6.5, 7, 7.5, 7.5, 7, 7, '0.8', 'sedang ke pelan', 'Acoustune AET08 (warm, more soundstage) Xelastec Sedna (brighter, Superb Fitting)', 'Senacoustic Copper', NULL);
