INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (73, 'salnotes-zero-2', 'B-', 'B-', 'Salnotes Zero 2', 379000, '3.5 + Mic', '', 0.8, '1 DD', 'Agak kemahalan sih untuk harganya.  mgkin kalo 270 masih sweet spot. resolusi better dibanding TE Gate, tuning sih oke ya mnurutku compare to seri 1 lebih ada bobot bassnya punchy. clarity treble masih ok bgt. sama fittingnya bikin pegel ada jendolan gitu kalo udah aga lama gaenak. resolusi midrange better than Gate dan Chu 2 Review Youtube IEM 100ribu hingga 1 Juta', 6.663636363636363, 6.3, '7', 6.5, 7, 6.5, 'Aman', 7, 6.5, 6.5, 6.5, 7, 6.5, 'Single Driver', 'sedang ke kencang', 'Acoustune AET08', NULL, NULL);
