INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (79, 'tripowin-piccolo', 'B', 'B+', 'Tripowin Piccolo', 525000, '3.5', '', 0.7, '1 Dual Cavity LCP DD', 'Iem baguss salah harga juga sebenernya tapi fitting kabel dan eartips kurang banget. preferensi tuningan gw pun mulai mengharga Neutral With bass boost seperti ini. tapi punch nya gak ilang samsek. enak. dari uppermidrange to ujung treblenya Piccolo itu bagus banget. smooth dia gak ada 1 gunung peak di frekuensi tertentu seperti blon Z300. Clean buat penyuka vokal. kekurangan. Connector QDC Pendek. enaknya ganti kabel 2pin aja. sama eartipsnya kurang pas fittingnya kurang enak. gw ganti ke Softears UC or aex70 langusng fine', 7.045454545454546, 7, '7.5', 7, 6.8, 7, 'Aman', 7.5, 6.7, 7, 7, 7, 7, 'Single Driver', 'sedang ke kencang', 'Softears UC - Better FIt & Midrange', 'Xinhs Single Crystal Copper Lyre Acoustics Primrose Senacoustic Copper', NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (80, 'blon-z300-hbb-full-review', 'B', 'B', 'Blon Z300 HBB  FULL REVIEW', 525000, '3.5 Type C', '', 0.7, '1 DD', 'Tuningan balance. sub bass tetap deep tapi gak lebay. warm. ada peak di 8k tapi tidak pengaruh sakit or menggganggu d gw. karena desain nozzle dan eartips bawaannya yg juga ok. fitting juara tapi bahan agak2 besi. kabel bawaan bagus. fun. soundstage cukup megah. bersaing dengan Moondrop Aria yang 1 jutaan. Bass Quality is Superb. kuanititas gak lebay. tapi detail dan getarannya dapet. midbassnya bisa jadi muddy kalo busy track.', 7.081818181818182, 7.5, '7.5', 7, 6.9, 6.5, 'Aman', 7, 7, 7.5, 7, 7.5, 6.5, 'Single Driver', 'sedang', 'Acoustune AET08 (M) ', NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (81, 'qkz-hbb-khan', 'B', 'B', 'QKZ HBB Khan', 550000, NULL, NULL, NULL, '2 DD', 'tight. asyik. upper midsnya makin jernih dari yang HBB biasa. cocok buat basshead. berasa energic dan boombasstic banget bassnya. fun . tapi bassnya kuantitasnya ga sebanyak hbb original. lebih ok walau masih banyak. tapi kualitas basssnya buagus. rounded and full. prefer ini daripada Truthear Zero. PUnchy . ada roll off bass dari 50hz bikin bassnya walau gemuk tapi recovery nya cepat. dan ada low mid cut di 200hz. ini trick biasa di mixing juga. kalo cut di sini akan beri kesan emphasize di low end jadi lebih banyak. keren!', 6.909090909090909, 7, '7.5', 6.5, 7, 6.5, 'Aman', 6.5, 6.5, 7, 7, 7.5, 7, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (82, 'moondrop-aria', 'B-', 'B', 'Moondrop Aria', 1000000, '3.5', '', 0.7, '1 DD', 'Gw masih prefer t3 plus. mid low nya bagus. berasa. walau secara graph kurg lebih berdekatan. tapi oh entah mengapa. ini karakternya Moondrop banget. low midnya kurang Greget kurang Oomph dikit. buat denger twangy2 gitar funk or rock ga berasa. ini fokus vokal cewe or cowo aja bersih. ngalain t3 plus. tapi kalo buat overall genre. I''d stcik to t3+ ini gw taro kitab karena banyak yg req aja', 6.909090909090909, 6, '6', 7, 7, 7.5, 'Aman', 7.5, 7, 7, 7, 7, 7, 'Single Driver', 'sedang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (83, 'harmonic-empire-xiao-qiao', 'B-', NULL, 'Harmonic Empire Xiao Qiao', NULL, '3.5', NULL, 0.65, '1DD', 'sebenernya drivernya bagus. bassnya memuaskan, teknikal juga good. cuma overemphasized aja di 4-6k, region sibilancenya berasa. buat low volume masih aman. buat moderate to high akan bersa banget crispy pedes di vokal, dan sometimes juga ganggu snare drum', 6.936363636363637, 7, '6', 7, 5.8, 7, 'Aman', 7.5, 7, 7, 7, 8, 7, 'Single Driver', 'pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (84, 'kinera-wyvern-putih-hitam-mic-test', 'B+', 'B+', 'Kinera Wyvern Putih/hitam  Mic Test', 359000, '3.5 + Mic', '', 0.8, '1DD', 'BEST IEM 300k balance warm tuning aman. tidak pedes. soundstage cakep udah sekelas iem2 500. walau gw merasa treble  bisa lebih airy dikit lagi tapi ini udah lebih dari cukup. jgn ambil yang ungu abyss or remaster. mendem. too dark . uppermids ketutup Review di Sini', 6.99090909090909, 7.5, '7', 6.8, 7, 6.5, 'Aman', 7.5, 7.2, 6.8, 6.8, 7, 6.8, 'Single Driver', 'sedang ke kencang', 'Softears UC + Clarity + Upper mids', 'VJC BPJS Hijau', 'EQ Wyvern');
