INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (100, 'tin-hifi-c1', 'C+', NULL, 'Tin Hifi C1', 299000, NULL, '', 0.7, '1DD', 'Driver tin hifi ini ga pernah jelek. timbrenya bagus2. walau murah gak kaya KZ CCA yg ringing metalik besi nya masih berasa bgt di treble. sialnya treble roll off lancip2 nya masi ada (Cek fernanda squiglink) lbh lumayan dr saga dan phoenix. but GK Kunten can definitely rival it', 6.694545454545453, 7.4, '7', 6.8, 6.74, 6.6, 'Aman', 6.8, 6.3, 6.5, 6, 7, 6.5, 'Single Driver', 'sedang ke kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (101, 'tanchjim-bunny', 'C+', NULL, 'Tanchjim Bunny ', 319000, '3.5 Type C', '', 0.29, '1 DD', 'Yet another 300k. upper mids to treble sentrik. punchy tp kuantitas bass ga banyak. very2 less bass. tanchjim banget pokoknya. treble  di drivernya agak kasar2 harsh driver terjangkau. bukan harsh as in boosted aja di region low tre/upmids tapi emg grainy2 kasar less resolving aja. prefer Kinera wyvern putih/tangzu wan er studio. a bit better than chu 2/TE Gate. pecinta vokalan less bass lah.  Full review di Youtube', 6.336363636363637, 6.5, '6.5', 6.5, 5.5, 7, 'Lumayan Ada', 6.5, 6, 6, 6.5, 6.2, 6.5, 'Single Driver', 'sedang ke pelan', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (102, 'kinera-wyvern-blue-skip-qing-abyss', 'B+', NULL, 'Kinera Wyvern Blue  Skip qing/abyss', 419000, '3.5', '', 0.65, '1DD', 'Kinera wyvern yang aman dibanding Qing dan Abyss ungu. ini better. bersaing banget dengna yang warna putih. dan warna putih org banyak ga suka warnanya. naini lah jawabannnya. enak. tonal balance banget dihajar volume tinggi. vokal lega. separasinya imagingnya untuk under 500 sih udah ok banget. timbre juga lebih natural dari yg putih. midbass agak banyak & masih agak ketutup dikit upper midsnya gak kaya yg putih. tapi udah aman banget kalo mau beli wyvern tapi ga suka warnanya yg putih', 6.963636363636363, 7.5, '7', 6.5, 7, 6.5, 'Aman', 7.5, 7.2, 6.8, 6.8, 7, 6.8, 'Single Driver', 'sedang ke kencang', NULL, NULL, NULL);
INSERT INTO "iem" (row_order, slug, "value", "sound", "iem", "price", "kabel", "musician_monitor_fit", "percentage", "driver", "review_notes", "total_sound", "bass", "lo_mid", "hi_mid", "treble", "voc", "sibilance", "soundstage", "imaging", "instrument_separation", "timbre", "punch", "detail", "multidriver_subjective", "linearity", "recommended_eartips", "kabel_recommend", "parametric_eq") VALUES (103, 'kz-dfi-mic-test', 'C', 'B', 'KZ DFI  Mic Test', 249000, ' 3.5 Mic', '', 0.65, '1 Dual magnet Dynamic Driver', 'JANGAN DIBELI LAGI BANYAK PROBLEM IMBALANCE tuningan ga v shape. bass quantity gak lebay. midrange vokal jernih. presence boost tidak sakit. becek banget. buntut reverb kedengeran jelas. gebukan enak. technical masi belom bisa kaya Tin Hifi C3 yg simbalnya gak harsh dan natural. di kz masi agak kasar dikit tpi ok bgt. good rec . ada seri yang Tunable (punya EQ Preset di body IEM nya, bisa naek turunin bass doang. udah gw fullin banyakan bass D-Fi non tunable sedikit. kalo lu suka se netral yg tunable sih mending ambil Tin C2 aja. sayang mnurut gw. Bisa saingi Blon Z300. Ganti eartips ke AET08 or Type E. Mid bass gak muddy kaya blon, lebih clean', 6.454545454545454, 6.5, '6.8', 6.5, 7, 6.5, 'Aman', 6.5, 6, 6, 6.5, 6.2, 6.5, 'Single Driver', 'sedang ke pelan', 'Dunu S&S better soundstage, midrange JVC Stress Free warmer, less treble & sub bass Dekoni Mercury FOAM Super less treble Acoustune AET08 - Midbass + Soundstage Final Audio Type E - Sub bass Extension', 'Kabel bawaan ok banget2. XINHS Single Crystal Copper Xinhs Rainbow Blue extra sub Senacoustic Copper', NULL);
