INSERT INTO "dongle_dac" (row_order, slug, "tier", "value", "dac", "price", "connector_type", "output_interface", "power_source", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_vs_digital_character", "power", "review_notes", "bluetooth_features", "microphone_input", "needs_amp_stacking") VALUES (13, 'timeear-bte-9-new-bluetooth', 'A+', 'S', 'Timeear BTE-9 NEW + Bluetooth', 1499000, '- Type C to Type C - Type C to Type A - Type C to Lightning', '4.4 3.5', 'HP/PC + Battery', 'Cirrus Logic CS43198', 'Neutral, Clean. energetic', 8.5, 8, 8.5, 8, 8.7, 8.3, 'A<<<>X>D', 8, 'Bagus. bisa bluetooth. kadang masih ada bug. buat konek appnya mesti donlod APK bukan playstore. belum Parametric EQ masih fixed frequency. suara ok sekelas kok sama Onix/Akliam. gak sejauh bagus bgt gitu. masih kalah dari Onix beta/cayin ru9 in terms of soundstage open ness. tapi powernya emg badag banget. mic buat telponannya juga oke. logika mode Blutut/USB nya kurang praktis. mesti nyala pas pairing, colok USB, play lagu, baru masuk mode USB. App is a little buggy, dan switching mode blutut/kabel jg suka error', 'YES', 'YES', 'Enggak');
INSERT INTO "dongle_dac" (row_order, slug, "tier", "value", "dac", "price", "connector_type", "output_interface", "power_source", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_vs_digital_character", "power", "review_notes", "bluetooth_features", "microphone_input", "needs_amp_stacking") VALUES (14, 'fiio-ka15', 'A', 'S', 'Fiio Ka15 ', 1750000, '- Type C to Type C - Type C to Type A - Type C to Lightning', '4.4 3.5', 'HP/PC', 'Cirrus Logic CS43198', 'Dry, natural, neutral, ', 7.8, 7.8, 7.8, 8, 8, 8, 'A<<<X>>D', 7.8, 'Exact same chip & amp dgn onix xi1 tp nth knp dmn aja onix tebel. luas gitu. ini stagingnya agak dry aja.  class AB nya cakep kaya tanchjim luna fitur ini bikin luas dikit. ada custom EQ. oke kok. decent banget for the price ngalain fitur2 ua4 dgn harga ga beda jauh. dan ada mic input juga. dan baterai juga lebih hemat powernya. great recommendation buat yg cari DAC dan amp nya bagus + tetep ada EQnya. ka17 lebih luas lebih tebel dan lebih smooth treblenya', '-', 'YES', NULL);
