INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (7, 'xduoo-xd05-pro-superb-sound-with-optional-mod-variety', 'S+', 'Xduoo XD05 Pro SUPERB SOUND WITH OPTIONAL MOD VARIETY', 12785000, 'USB C', '- 6.3 -4.4', 'DAC/AMP', 'Default:  ESS ES9039SPRO  Additional: - AAKM K4499EX - ROHM BD34301 - R2R (coming soon) - Cirrus Logic (soon)', 'Tight Open, Punchy, energetic,  wide', 9, 9, 9, 9.5, 8.5, 9, 'A<<<>X>D', 'KEREN PARAH! Bisa ganti DAC Chip & OP AMP dan Bisa pakai baterai. Default soundnya udah enak apalagi di mode paling high gain (Turbo). bisa sembari ngecas, or external power pake charger type c. bs blutut. ada mic nya buat telponan. Default sound ESS nya bener2 detail fullrange analytical detail dan open punchy aggressive. buat di AKM nya agak lebih smooth laidback. sweet tapi vivid separation. Ga butuh PURE DAC dan PURE AMP Terpisah dan praktis dan kecil sebenernya buat di desktop. bener2 flexibel pula ke depannya modular tinggal gnti chip dan OP amp aja sendiri gampang banget', 'LDAC APTX HD APTX LL AAC SBC');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (8, 'topping-dx5-ii-highly-recommended', 'A+', 'Topping DX5 ii HIGHLY RECOMMENDED', 4899000, '- Usb C - Coax - Optical', '- XLR - RCA - 6.3 - 4.4', 'DAC/AMP', 'ESS Sabre ES9039Q2M ', 'Neutral Warm, Full, black background', 8.5, 8, 7.8, 8, 8, 7.8, 'A<<<>X>D', 'Parametric EQ! Bisa simpan up to 8.lebih full bodied gitu suaranya dibanding SMSL RAW. staging ditest pake ATH/Sennheiser oke2 bgt. ga thin. neutral slightly warm, fuller sounding. bisa EQ2 ok banget. gak segemuk tebel Shanling EC Mini, tp pas aja. black background silent. detail oke untuk harganya', 'LDAC APTX AAC SBC');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (9, 'smsl-raw-mda1', 'A+', 'SMSL RAW MDA1 ', 4550000, '- Usb C - Coax - Optical', '- XLR - RCA - 6.3 - 4.4', 'DAC/AMP', 'ESS Sabre ES9039Q2M ', 'Neutral to bright, Transparent. adjustable sound color', 8, 8, 7.8, 8, 8, 7.7, 'A<<<>X>D', 'Nice filter ngaruh plus ada kaya Sound Colour gitu ga tau kaya Mini EQ preset atau jatohnya kaya plugin mixing ada rasa2 tube/tape/transformer drive. tapi enak2 banget. defaultnya neutral to bright. really transparent dinamika engaging. enak deh. gw suka di mode Tube 2. enak midnya jadi lebih berisi tapi treblenya gak semenyerang yang standartd', 'LDAC APTX AAC SBC');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (10, 'smsl-d300', 'A', 'SMSL D300', 6400000, '- Usb A - Coax - Optical', '- XLR - RCA - 6.5 - 3.5', 'Pure DAC', 'ROHM BD34301EKV', 'Detail, revealing, Neutral', 7.6, 8, 8.2, 7.8, 8, 9, 'A<X<>>>D', 'review di youtubenya sangat menggiurkan dan sound comparisonnnya pun sangat mampu melawan DAC lain di atas harganya. bener2 natural analog ish banget suaranya lega. Open. tapi grit greget masih dapet. cocok buat ke speaker. gw udah try juga di Headphone via amp tambahan ifi dan JVC VN900. asyik banget banget.. Chip ROHM nya cakep sudah seperti merk Luxman dan Marantz yang udah 10ribu dollar ke atas.. gw ga pernah compare si tapi dimana2 ini reviewnya cakep. jadi gw sikat aja buat speaker gw.', 'LDAC APTX AAC SBC');
