INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (12, 'fiio-k11-sweet-spot-newbie-desktop-dac-amp', 'A+', 'Fiio K11 Sweet Spot Newbie Desktop DAC AMP', 2250000, 'Type C to Type A Coax Optical', '4.4 3.5 Coax', 'DAC/AMP', 'Cirrus Logic CS43198', 'Neutral, Energetic', 7, 7.5, 7.5, 7, 7, 7.5, 'A<<<>X>D', 'Sala satu chip flagship Cirrus dan ad di soundcard gue Antelope Zen Go (9jutaan) Suaranya fun. greget, gritty. garang. oke banget. competitor to the warm Ifi Zen Dac. I really like Zen Dac''s detail and warmth. but This new competitor  is really good as well with high value. ada optic buat jadi dac CD Player. separate power jadi mgkin bisa cegah ksetrum or ground loop dibanding colok laptop langsung. ada balancenya. enak. mid clean. garang. gritty', '-');
