INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (14, 'topping-g5', 'B', 'Topping G5', 4500000, 'USB C  Bluetooth LDAC AUX IN', '4.4 3.5', 'DAC/AMP', 'ESS Sabre ES9068AS ', 'Neutral Bright', 7.5, 7.8, 7.5, 9, 7.2, 8, 'A<<<>>XD', 'Hybrid power. ada batre. or bisa sembari colok. tempat cas terpisah jadi gak kuatir sama baterainya. Jernih powerful. MEGAH LEBAR. jernih. prefer ini secara value for money juga dibanding DAP mahal kaya Fiio M11, M17, or even Astell Kern SP2000 (sangat subjektif)', 'LDAC AAC SBC');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (15, 'topping-d50s', 'B', 'Topping d50s', 2650000, '- Usb A - LDAC Bluetooth', 'RCA', 'Pure DAC', 'Ess Sabre ES9038Q2M', 'Neutral, tight, Aggressive, forward', 7, 7, 7.5, 7, 7, 7, 'A<<<>>XD', 'Ini biasa gw pake di PC gw. atau kadang gw jadiin blutut receiver buat edifier r1700 gw. suaranya cenderung netral dan FUN apa ya? dinamikanya jadi fun. kenceng keras mainnya jadi lebih asyik di dengernya. bisa jadi blutut receiver. bisa jad mode USB juga. enak dah. mantep abis. tapi kadang gw lebih suka DAC di soundcard SSL2 gw (pake AKM) krn midnya lebih ad. tp gw pake ini. biar bisa distack ke amplifier A50s juga sih.', 'LDAC AAC SBC');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (16, 'topping-dx1', 'A', 'Topping DX1', 1500000, '-Type B to Type A (Included)', '3.5 6.35', 'Dicolok ke hape or PC', 'AKM AK4493s', 'Punchy, Aggressive, Forward', 7, 6.5, 7, 7, 6.5, 6.5, 'A<<<>>XD', 'Attack nya asyik. bikin bass jadi makin dalem dan thumpy. menyenangkan. seolah pemain musik yang lagi maenin drum, gitar dll itu maennya lagi semangat aja. powerful. as in suara ya bukan powerful as in impedance. kaya menenangkan fun banget. ', '-');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (17, 'fiio-q11', 'A', 'Fiio Q11', 1450000, '- Type C to Type C - Type C to Type A - Type C to Lightning', '4.4 3.5', 'Dicolok ke hape or PC', 'Cirrus Logic CS43198', 'Midrange Centric, Less sub', 6.8, 7, 7, 7.5, 7, 6.8, 'A<<<X>>D', 'DAC Pertama gw adalah Fiio q3, dan Topping NX4. gw suka bgt bentuk kecil. dan gw sangat suka DAC AMP bentuk powerbank mini gini di karetin di blakang hp. karetnya fiio ini bagus, gak ngalanin layar. suaranya megah. dengan emphasis midrange yang open banget rasanya. lega sekali dibanidng Lotoo paw dan ifi go bar. semoga makin banyak DAC amp bentuk gini dan setup tumpuk karet lagi. dan harganya terjangkau. ktimbang keluar bajet ifi go bar 4 juta cuma dapat dongle.', '-');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (18, 'ifi-uno', 'A-', 'Ifi Uno', 1264000, '-Type C to Type C -Type C to Type A', '3.5', 'Dicolok ke PC or HP', 'ESS Sabre', 'Less Sub, Neutral, Upper mids Boost', 7.5, 7.5, 7, 7.5, 6.8, 7, 'A<<<>>XD', 'Suaranya sweet. santai.. gak se aggressive and punchy and powerful kaya Topping DX1. mereka berdua sekelas. cuma gw prefer si DX1 bukan power impedance ya. tapi si punch bass. dan suaranya itu lebih nendang aja di DX1. di sini santai, sweet. gak jelek kok. cuma preferensi,, dan tergantung IEM lagi juga. ada 3 mode EQ preset. gak ada yang gw pake sih.', '-');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (19, 'fosi-audio-q4', 'B++', 'Fosi Audio Q4', 1000000, '-Type B to Type A (Included)', '3.5', 'Dicolok ke PC or Laptop', 'MS8412', 'warm', 6.5, 6.5, 7, 6.5, 6.5, 6.5, 'A<<<>>XD', 'GELO! Benda Murah bin ajaib dan powerful! Chip DAC nya kagak terkenal sama sekali suaranya deket sama Ifi Uno. tapi ada EQ bass dan Treblenya analog dan enak banget. buat ngetame down treble2 iem/headphone yang kurang. or malah buat boost bass juga bisa bebas. bisa cut or boost. bisa ambil input dari TV Optical  or coaxial or CD Player juga. bisa jadi preamp spekaer edifier yang pokoknya ada RCA nya deh. suara udah better banget daripada colok PC langsung. dibanding sma Uno, lebih agak lega open uno sedikit tapi tipiiiiiisss banget. kebanting sih sama fitur2 Adjustable bass dan Treble + jadi preamp + konektifiti yang beragam', '-');
INSERT INTO "desktop_dac" (row_order, slug, "value_rank", "dac", "price", "digital_input", "output_interface", "pure_dac_or_dac_amp", "chip", "sound_description", "subjective_sound_rating", "dynamics", "punch", "clarity_detail", "soundstage_openness", "instrument_separation", "analog_digital", "review_notes", "bluetooth_features") VALUES (20, 'toppng-d30-pro', 'B', 'Toppng d30 Pro', 6000000, '- Usb A', 'RCA  XLR', 'Dicolok ke PC', 'Cirrus Logic CS43198x4 Quad', 'Neutral, Tight, Forward', 7, 7, 7, 7, 7, 6.5, 'A<<<>>XD', 'Netral tapi jernih banget. tapi jernihnya gak diboost gitu kok asli enak banget. quad chip kalo ga salah artinya ada 4 chip jadi masing masing channel kiri dan kanan dapat 2 chip. ini bikin megah dan lebar dan detail banget sih asli. gw lagi nabung buat beli ini juga wkwkwkkwkw. ada Rca out ada balance XLR out juga. ', '-');
INSERT INTO "gaming_headsets" (row_order, slug, "rank", "headphone", "type", "price", "review_notes", "bass", "lo_mid", "hi_mid", "treble", "voc", "technicality", "driver_quality", "fitting", "tuning", "microphone", "wireless_capability", "spatial_surround", "app", "eq") VALUES (1, 'audeze-maxwell-wireless-new', 'S++', 'Audeze Maxwell Wireless NEW', 'HP Wireless, HP Kabel', 4999000, 'God Tier gilak well packed feature. batre 80jam. ai mic. detachable mic. custom EQ di app HP maupun desktop. Adjustable mixer buat sidetone dengerin suara sendiri + game. Dongle Type C anti delay. bisa dicolok ke PC, HP, PS', 8, 8, 9, 9, 8, 9, 9, 8.5, 'Neutral', 10, '- Bluetooth - Dongle Type C', 'YES', 'YES', 'Graphic EQ');
INSERT INTO "gaming_headsets" (row_order, slug, "rank", "headphone", "type", "price", "review_notes", "bass", "lo_mid", "hi_mid", "treble", "voc", "technicality", "driver_quality", "fitting", "tuning", "microphone", "wireless_capability", "spatial_surround", "app", "eq") VALUES (2, 'hyperx-cloud-3-very-good-mic', 'A', 'HyperX Cloud 3 Very good mic', 'HP Kabel, HP Wireless', 1341000, 'Neutral tuning. fitting super ok nyaman di gw. mic juga bagus banget. yang versi kabel agak susah diangkat dikit volumenya. Volume mic juga aga pelan dibanding yang wireless. sisanya kurang lebih sama. yg wired ada semacam soundcard trrs sendiri. very2 neutral tuning. less sub bass. more treble dikit. less upper mids. prefer upper mids naek dikit. Better than Inzone H3 neutralitynya. gak sebright h3', 7.8, 8, 7, 7.8, 7.4, 8, 8, 8.5, 'Balance', 9, ' - Dongle Type C', NULL, '-', '-');
INSERT INTO "gaming_headsets" (row_order, slug, "rank", "headphone", "type", "price", "review_notes", "bass", "lo_mid", "hi_mid", "treble", "voc", "technicality", "driver_quality", "fitting", "tuning", "microphone", "wireless_capability", "spatial_surround", "app", "eq") VALUES (3, 'sony-inzone-h3', 'A', 'Sony Inzone H3', 'HP Wireless', 979000, 'Ada H3,H7, H9 seri gamingnya Sony cuma menurut gw value for money lu ambil h3 aja. lebih murah jauh. ga perlu wireless. mic juga jauh lebih bagus dibanding wireless. kekurangan gak ada ANC or blutut. tp bagi gw itu malah bukan kekurangan. ada App buat custom EQ, atur gain, sudah DISCORD Certified juga. mic bagus', 8, 8, 7, 6.8, 7, 8, 8, 8.5, 'Balance', 9, 'NO', 'YES', 'YES', 'Graphic EQ');
