INSERT INTO "gaming_headsets" (row_order, slug, "rank", "headphone", "type", "price", "review_notes", "bass", "lo_mid", "hi_mid", "treble", "voc", "technicality", "driver_quality", "fitting", "tuning", "microphone", "wireless_capability", "spatial_surround", "app", "eq") VALUES (7, 'edifier-k800', 'D', 'Edifier K800', 'HP Kabel', 159000, 'Edifier K800 ini termasuk barang yang gw coba-coba beli tapi bagus. murah meriah buat yang suka meeting. phone call. discord. micnya bagus banget asli. bersaing sama dua yang di atas.  sama satu lagi suaranya juga cakep banget banget... asli ini mah udah kaya headphone 500an ke atas. balance. gak perih. tapi bukan tanpa kekurangan. case plastik. di headband gak ada padding buat bbagian rambut. dan earpadsnya on ear.. agak kurang nyaman tapi masih oke banget buat yang "sekedar ada headphone"', 6, 6, 7, 6.5, 6, 5.5, 6, 6, 'Balance Warm', 9.5, 'NO', 'NO', 'NO', 'NO');
