INSERT INTO "microphones" (row_order, slug, "rank", "microphone", "price", "shopee_tiktok", "type_capsule", "connection", "needs", "review_notes", "polar_pattern", "dipakenya_gini_nih_blay") VALUES (23, 'triton-d1', 'C++', 'Triton D1', 850000, 'Shopee', 'Dynamic', 'USB Type C XLR', '- Voiceover - Studio Vocal - Podcast', 'Daripada lu beli mic butek Shure MV7 mending ini. udah murah jauh. kalo diupload ke konten sosmed juga udah kaya ga da bedanya. sama SM7B yang 6 jutaan pun ngelawan. mid ama presencenya kalah ama SM7B tapi once masuk sosmed bener2 gak ketara. cek video review gw aja. apalagi kalo lu pake soncard. or EQ dikit2 udah aman banget ini buat pemula. kurang di rejectionnya aja dikit sih. gak sebagus SM58 ', 'Cardioid', 'Gaming, Podcast, Vokal di Studio, Meeting');
