INSERT INTO "microphones" (row_order, slug, "rank", "microphone", "price", "shopee_tiktok", "type_capsule", "connection", "needs", "review_notes", "polar_pattern", "dipakenya_gini_nih_blay") VALUES (25, 'shure-mv7', 'C', 'Shure MV7', 3450000, '-', 'Dynamic', 'USB Micro XLR', '- Voiceover - Studio Vocal - Podcast', 'I know shure is a very good brand. tapi ini mic butek cuk. sama jangan taro atas meja pake tripod. deketin mulut. sebenernya aman2 aja sih. cuma warm banget. SM7B aja udah super duper warm. dan ini di bawahnya..  cumba karena dia versi rendahnya dibbikin gainnnya gak se low SM7B yang berat. karena ada USB nya juga. ya kalo looks is your thing... bisa aja ambil ini. rejectionnnya bagus. cuma mgkin gw prefer AKG Ara dan or murahnya ATH 2100x', 'Cardioid', 'Gaming, Podcast, Meeting');
