INSERT INTO "microphones" (row_order, slug, "rank", "microphone", "price", "shopee_tiktok", "type_capsule", "connection", "needs", "review_notes", "polar_pattern", "dipakenya_gini_nih_blay") VALUES (28, 'fifine-t730', 'D', 'Fifine T730', 490000, NULL, 'Condenser', 'USB', '- Voiceover - Podcast', 'Kata gw mah ya lu beli ini aja klao cuma buat iseng. gak serius. ga mau jadi musisi, or podcaster handal, or voiceover artist. udah beli ini aja kalo buat discord, meeting , live main game doang. kenapa gw suruh specifically yang ini? ada standnya. bisa deket ke mulut jadi gain lu gak perlu gede. ke-2 lu langsung punya stand dan pop filter. next nya perlu upgrade, ga perlu beli lagii. ', 'Cardioid', 'Zoom Meeting, gaming, sosmed content');
INSERT INTO "microphones" (row_order, slug, "rank", "microphone", "price", "shopee_tiktok", "type_capsule", "connection", "needs", "review_notes", "polar_pattern", "dipakenya_gini_nih_blay") VALUES (29, 'soundtech-2-1', 'D++', 'Soundtech 2.1', 499000, NULL, 'Condenser', 'USB', '- Voiceover - Podcast', 'Ini salah satu mic USB local yang brandnya cukup mendengarkan banyak feedback dari consumernya. dia benerin fungsi echo dari versi sebelumnya yang agak mengganggu sekarang udah jadi better. gw pake ini waktu WFH gak d rumah. not bad buat komunikasi, meeting, maen game, jangan teriak2 aja kaya agak mudah distorsi di gain tertentu.', 'Cardioid Omnidirectional', 'Zoom Meeting, gaming, sosmed content');
