INSERT INTO "headphone_amps" (row_order, slug, "rank", "headphone_amp", "price", "input", "output_interface", "feature", "review_notes") VALUES (1, 'zmf-homage', 'S+', 'ZMF Homage', 21000000, '- RCA - XLR ', 'XLR 4.4', 'Impedance selector, Hi/med/lo gain, mute ', 'Neutral open. slightly dry. tapi tetep detail dan open aja sih. cuma rasanya emang kurang sweet dan lega. gak kaaya spaciousnya si Aune S17. tapi aune panas. jadi gw ganti ini. Agak kemahalan harganya untuk OP amp based, tp suaranya sih sabi bgt dan desainnya unik.');
INSERT INTO "headphone_amps" (row_order, slug, "rank", "headphone_amp", "price", "input", "output_interface", "feature", "review_notes") VALUES (2, 'aune-s17', 'S+', 'Aune S17', 11500000, '- RCA - XLR ', 'XLR 4.4 ', 'Class A, High gain, Low gain', 'Sangaaaattt luasss 3d spherical imaging. it is very apparent saat gw coba pakai Audeze mm100 lebar luas, compare to langsung dac/amp fiio or Topping or SMSL langsung. keliatan jelas vokalnya ke depan jauh. musiknya kanan kiri belakang depthnya dapet. very open banget. Minus utamanya sangat panasssss');
INSERT INTO "headphone_amps" (row_order, slug, "rank", "headphone_amp", "price", "input", "output_interface", "feature", "review_notes") VALUES (3, 'topping-a90-discrete', 'S', 'Topping A90 Discrete', 9000000, '- RCA - XLR ', '- XLR 4 Pin Bal - 4.4 Bal - 6.35 Single ended', 'High gain, lo gain,  act as preamp for speaker as well Remote control', 'Power sangat cukup untuk almost every headphone out there. highnya jernih and supper smooth and gak aggressive at all. coba hd650 di sini vs zen can signature. zen jadi lebih tajem. dan nusuk. A90D lebih cakep. sub bass tebel. mid spacious lebar. atasnya gak nyerang sama sekali. ajaib. cocok juga dicombo sama DAC2 mahal kaya Gustard r26. ');
INSERT INTO "headphone_amps" (row_order, slug, "rank", "headphone_amp", "price", "input", "output_interface", "feature", "review_notes") VALUES (4, 'ifi-zen-can-sennheiser-signature', 'S', 'Ifi Zen Can Sennheiser Signature', 2690000, '- RCA - 4.4 Bal ', '- 4.4 Bal - 6.35 + 3.5 converter', '4x High gain Power match Analog EQ Correction for HD6xx/HD650 3d X Space', 'Good match for Ifi zen dac v2. gausa beli yang signature kalo zen dac nya. lebih banyak fitur yang zen dac default nya. zen can signature ini cocok buat HD650/hd6xx user yg pengen dapet eq correctionnya. pas. mantep. suara warm upper mids clean. ');
