INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (2, 'kef-lsx-ii-fernanda-s-main-home-audio-listening-speaker', 'SS', 'KEF LSX ii Fernanda''s Main Home Audio Listening Speaker', 24000000, '- Listening', '- Small Living Room - Nearfield Desk', 'Active', 'Streamer, Airplay, spotify connect, free radio stream. USB DAC. Bluetooth. keren. very transparent mids, great clarity. superb soundstage. amazing rumble just enough to get you shiver. Ini mid to highnya transparan parah. jelas parah jernih claritynya terllau jernih. duduk dimanapun kuping lbh tinggi dari speaker, agak kebawah, agak ke kiri/kanan tetep konsisten suaranya. tweeter taro depan midwoofer, teknologi KEF yg paling gw suka.', '- SVS 3000 Micro - KEF KC62', '- Wifi - Optical - 3.5 - Bluetooth', NULL, 'Position adjustment, LAN', 'YES', 'NO', NULL);
