INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (4, 'adam-audio-d3v-fernanda-s-main-mixing-speaker', 'S', 'Adam Audio D3V Fernanda''s Main Mixing Speaker', 6700000, '- Mixing - Listening', '- Nearfield Desk Setup', 'Active', 'Gilak! Speaker kecil tergila yang pernah gw denger. directivity detail dari AMT drivernya. response cepetnya. huge sound from a small form factor speaker. Bisa USB, bisa TRS, tapi dari TRS ttp diubah lagi ke digital, kena DSP, kena DAC nya lagi. tp ttp bs beda suaranya dibanding direct USB. jujur banget speaker ini. bass ga akan kaya speaker inch gede. tp berasa cukup bgt dgn passive radiatornya di kondisi nearfield desk kecil', 'NO', 'TRS USB', 'NO', 'Room Control, Position, Desk. Auto Standby. baut ke stand mic', NULL, NULL, NULL);
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (5, 'polk-signature-elite-es20', 'S', 'Polk Signature Elite ES20', 7200000, '- Listening', '- Medium Living room ', 'Passive', 'Ini koleksi pribadi gw pesen sendiri. Neutral2 masih se arah sama HS8. lebih Punchy mid bassnya di HS8. tapi midrange nya cakep banget. vokal sweet banget di sini. treble juga gak kerasa warm tetep jernih. bass gak banyak tapi udah OK. dan kalo kurang ya pakai EQ aja di Amplifier. wajib di dengerin nearfield. ', 'NO', 'Speaker Cable', 'NO', 'NO', 'NO', 'NO', '-');
