INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (8, 'edifier-r1700bts', 'A++', 'Edifier R1700BTs', 2200000, '- Listening', '- Medium Living room - Bedroom', 'Active', 'Upgrade nya dari R1700 yang lama.. ada sub out. dan extra line out.. midrange centric and crunchy. more open and spacious than 1280dBs dan tetep bisa dikasih subwoofer out. kalo mau vokalan ambil 1700 mau kaya lebih EDM an bisa r1280dbs cuma kalo 22nya lu kasih sub tetep enak2 aja cek this one out', 'Edifier T5', 'RCA Optical Coax', 'YES', 'EQ Low and High', 'YES', 'NO', '-');
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (9, 'edifier-r1280dbs-garansi-3-tahun', 'A++', 'Edifier R1280DBs Garansi 3 tahun', 1899000, '- Listening', '- Medium Living room - Bedroom', 'Active', 'Ya ini seri paling legendaris R1280db yang pengen punya speaker punchy mid bass. gw prefer r1700 sedikit sebnernya midrangenya lebih clean utamanya kalo akan pakai T5 Subwoofer sih sikat aja. yg antara 1280 or r1700 gak akan ada yang rugi', 'Edifier T5', 'RCA Optical Coax', 'Yes', 'EQ Low and High', 'YES', 'NO', '-');
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (10, 'edifier-r12u-new-recommended', 'A', 'Edifier R12u  NEW RECOMMENDED', 268000, '-Listening', '- Nearfield Desk Setup', 'Active', 'SANGAT REKOMENDED. buat anak kosan. garansi 3 tahun. Hanya orang aneh yang ga punya bajet, kebanyakan nanya, tapi ga beli ini. 2 channel. lebar. midbass lebih berasa dari spiker logitec yg murmer2. cocok buat setup kecil2an yang spacenya gak gede. kaya lesehan pake laptop d kosan. jauh lebih bagus dari speaker blutut 1 unit. ga berasa imaging lebarnya. dan masih bisa pakai headset kabel juga. jangan tanya. sudah cekout saja. gak ada bajet kebanyakan mau. ini udah sangat bagus. dilarang mikir. udah cekout pk link gw', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (11, 'mackie-cr5-x', 'B+', 'Mackie-CR5-X', 3750000, '- Mixing - Listening', '- Nearfield Desk Setup', 'Active', 'Ini lebih fuller lagi range frequenncynya dibanding CR4-x tapi menurut gw sih yaa CR4 udah cukup banget kjalo bajet lu di bawah 3 juta dan tentu saja masih butuh beli stand dan lain2. dia 5inch hampir sama dengan Yamaha HS5. but ini lebih ada low endnya. buntuk sub bassnya masih berasa di sini, HS5 lebih cleaner sound, lebih fast transient jadi kedengerannya, but I think kalo lu banyak mixing elektronic  music, sub bass, bass jazz lebih kedengeran di sini di banding HS5. can see this video for reference.  https://www.youtube.com/watch?v=AgqRjiNnoVU', NULL, 'RCA TRS', 'NO', NULL, 'NO', 'NO', '-');
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (12, 'yamaha-hs-5', 'C++', 'Yamaha HS 5', 3800000, '- Mixing - Listening', '- Nearfield Desk Setup', 'Active', 'ini sebenernya bagus, reference monitor banget yang cenderung neutral, but cukup berasa minim sub bassnya.. that is why gw selalu suka HS8 dibanding ini. or HS7, but then again. kali aja lu gak banyak mixing, low end, sub bass. mungkin lagu2 lu cenderung akustik yang isinya gitar dan vokal doang, or mixing voiceover doang bisa aja pakai ini. but I storngly recommend Mackie CR4-X for value for money. or CR5-x sekalian buat dapetin sub bassnya.. and kalo lu lagi gak mixing, denger lagu di CR5-x lebih berasa low endnya. NOT BOOSTED yah. lebih ADA. jadi lu buat nonton bisa, denger musik masih enjoy, mixing pun bisa sampe ke sub bass low end. There is no point in buying this especially di harga 3,8 juta lagi bagi gw... but it''s just me. but jujurly bener high nya or upper midnya clear banget for vokal mixing. Bisa jadi opsti buat yang suka mixing dengan 2 pasang speaker, 1 for full band (HS8), 1 lagi (hs5) for vokal/voiceover/podcast suara manusia only.', NULL, 'XLR TRS', 'NO', 'Room Control (semacam Low Cut) High Trim (semacam high cut)', 'NO', 'YES', '-');
INSERT INTO "stereo_speakers" (row_order, slug, "value", "speaker", "price", "needs", "posisi_dengerin", "type", "fitur_dan_review", "saran_subwoofer", "connection", "bluetooth", "additional_feature", "sub_out", "headphone_out", "soundtest") VALUES (13, 'mackie-cr4-x', 'B', 'Mackie CR4-X', 2650000, '- Mixing - Listening', '- Nearfield Desk Setup', 'Active', 'Bagi yang butuh monitoring murah meriah bisa pakai ini.. at least beli yang ini sih kalo menurut gw dibanding yang CR3 karena terlalu tipis suaranya kurang gak sampai ke sub. jadikalo mixing bass. or drrum gitu kurang berasa...', NULL, 'RCA TRS', 'NO', NULL, 'NO', 'NO', '-');
