INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (5, 'boya-magic', 'B+', 'Boya Magic', 1400000, '3.5, Type C, Lightning', 'Ini ok. belum ada limiter dan internal recording. Tp cukup praktis form factornya. kaya mini sennheiser profile wireless.  Suara cakep, noise reduction juga udah bagus di Weak. ada CUSTOM EQ juga.  Tonton review Youtube disini', '80dB', '115 dB', 'Weak & Strong', 'Bisa shotgun, handheld, desktop, maupun cllip on. ');
INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (6, 'godox-cube', 'B+', 'Godox Cube', 1499000, '3.5, Type C, Lightning', 'Ini menurut gw lebih bagus dari lark m2 mic nya. bagus banget. gw geser ke bawah dikit ke arah dada, masih dapat low end frequencynya di suara gua. Gas aja, jauh lebih murah pula dari M2 dan M2s. dan casingnya lebih kecil lagi buat dibawa kemana mana kaya case tws. Review di sini', '75 dB', '115 dB', 'Weak & Strong', NULL);
INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (7, 'hollyland-lark-a1-best-newbie', 'A+', 'Hollyland Lark A1 BEST NEWBIE ', 610000, 'Type C, Lightning', 'Selamat tinggal BOYA dan TAKARA. buat phone user, live streamer yg pake HP iphone/android kelar banget. harga startingnya tanpa case cuma 600k. ini harga launching. ada EQ preset, ada Reverb, ada AUTO LIMITER. jadi kalo teriak2 lg live, aman gak peak. auto kena gain reduction. bisa dipake sembari cas juga', '67 dB', '120 dB', 'Weark, Med, Strong', NULL);
INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (8, 'hollyland-lark-m2s', 'B', 'Hollyland Lark m2s', 2400000, '3.5, Type C, Lightning', 'Ini bedanya sama yang M2, adalah dia bentuk mic nya ngejepit gitu bukan diapit magnet. jadi langsung auto jepit di baju maupun kemeja oke. cuma kalo jepit kaos kan jadi agak terlalu ke ats tuh biasanya agak muddy/muffled suara kitanya krna deket tenggorokan, baeknya kalo kaos agak turun dikit, seperti M2 dimagnetin ke situ biar clear. di sini ga bisa. cuma ini enaknyha praktis dan ada noise reduction diatur lewat app hape Review M2S VS M2', '70 dB', '116 dB', 'Weak & Strong', NULL);
INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (9, 'hollyland-lark-m2', 'B', 'Hollyland Lark M2', 2056000, '3.5, Type C, Lightning', '- kaya TWS dimasukin ke dalam case charging gitu. cakep sih suaranya udah oke. bentuknya kaya kancing, bisa dikalungin included kalungnya.  bisa pakai magnet. bisa dijepit juga. enak buat attach kemana2 tapi tetep low profile . Ada noise reduciton tapi too sensitive dan agak bikin suara gua tenggelem.', '70 dB', '115 dB', 'Weak & Strong', 'Banyak varian, kalo mau ke HP Type C doang, or mau ke Lightning doang, or mau ke 3.5 kamera doang, or paketan 33nya');
INSERT INTO "wireless_clip_on_mics" (row_order, slug, "rank", "microphone", "price", "connection", "review_notes", "signal_to_noise_ratio", "sound_pressure_level", "noise_cancellation", "dipakenya_gini_nih_blay") VALUES (10, 'rode-wireless-go-2', 'B', 'Rode Wireless Go 2', 4290000, '3.5, USB', 'sebenernya gw pake yg seri 1. Buat live tiktok. tapi beda harganya ga jauh, mending lu ambil yg 2. utamanya sih di GAIN. di yg seri 1 itu ga bisa diturunin lagi. rentan pecah kalo lu teriak or ketawa. dan di seri 2 lu ada safety track. channel kiri gain sebenarnya, channel kanan menangkap suara yg sama tapi gain lebi rendah. jadi kalo dari ngomong tiba2 lu ktawwa kenceng terus di kiri ngepeak. ya lu ambil aja di safety track yang kanan. rekaman lu aman dari peak. dan fiturnya banyak dah. bisa diatur software juga. dan lu bisa pake buat 2 orang kalo beli yang ini', NULL, '112 dB', NULL, 'Omnidirectional, artinya menangkap segala arah sejujurnya kalo lu pake clip on. tp since ini gabung sama transmitter, agak semi directional juga sih. beli paketan yang ama magnetnya. biar nempel d baju enak, kalo lu gak punya lavalier mic. Pake clip on itu enak. lu ga perlu ngamanin headroom di kamera. karena gak bakal bocor mic shot gun dari atas kan. dan bisa ambil wide angle. shot jauh. tanpa khawatir ada mic bocor d kamera. Sialnya tp dia menangkap suara dari segala arah. pastikan suara d sekliling or studio itu kedap');